mapper.update:
UPDATE lifesite.magagine_attach
SET TITLE_YN = 'N'
WHERE POST_ID = #{postId}
AND POST_TYPE =#{postType}
AND TITLE_YN ='Y'

mapper.select:
SELECT *    /* post.RegisterMapper.loadById */
FROM lifesite.magagine_attach
WHERE ATTACH_ID = #{fileId}
AND POST_TYPE =#{postType}

